package controlstatement;

public class LoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=10;i++)
		{
			System.out.println(i);
			
		}

		int j=1;
		while(j<=10)
		{
			System.out.println(j);
			j++;
		}
		
		int k=1;
		do
		{
			System.out.println(k);
			k++;
			
		}while(k<=10);
		
		
		
	}

}
