package controlstatement;

public class LoopDemoTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 10; i > 0; i--)
			System.out.println("Hello World "+i);
		int a[] = {10, 20, 30, 40, 50};
		int b = 0;
		while (b < a.length){
			System.out.println(a[b]);
			b++;
		}
	}
	
	
	
	
	

}
