package operpack;

public class LogicalDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int a=101;
		int b=201;
		
		if(a>100 && b >200) //both should be true
		{
			System.out.println("Both a and b are greater than 100 and 200");
		}
		
		
		if(a>100 || b >200) //any one should be true
		{
			System.out.println("Logical OR  a or b is greater ");
		}
		
		
		

	}

}
