package operpack;

public class OperDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=10;
		
		int y=20;
		
		System.out.println(++x); //unary operator prefix
		
		
		System.out.println(--y); //unary operator prefix
		
		int xx=100;
		int yy=200;
		
		
        System.out.println(xx++); //unary operator postfix
        System.out.println(xx);
		
		System.out.println(yy--); //unary operator postfix
        System.out.println(yy);
		
	}

}
